BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here -- DELETE FROM Catalogos.Productos WHERE ID = @ProductId
UPDATE Catalogos.Productos SET Activo = 0 WHERE Id = @ProductId END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here
DECLARE @Counter AS INT;
BEGIN TRAN
BEGIN TRY SET @Counter = (SELECT COUNT(*) FROM [Catalogos].[Condicion_Pago] A WHERE UPPER(LTRIM(RTRIM(A.Nombre))) = UPPER(LTRIM(RTRIM(@Nombre))));
IF @Counter > 0 BEGIN SET @Error = -1 SET @ErrorMessage = 'Ya Existe un concepto de pago con el mismo nombre'; END ELSE BEGIN SET @Error = 0;
INSERT INTO [Catalogos].[Condicion_Pago] (Nombre) VALUES(@Nombre) END
COMMIT END TRY BEGIN CATCH
ROLLBACK TRAN SET @Error = -1 SET @ErrorMessage = (SELECT ERROR_MESSAGE()); END CATCH
END

