CREATE PROCEDURE [Catalogos].[InsUsuario] 
	-- Add the parameters for the stored procedure here
	@Nombre varchar(60),
	@Pass varchar(60),
	@Error INT OUTPUT,
	@ErrorMessage VARCHAR(200) OUTPUT
AS

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here
SELECT E.[Id], E.[Nombre],E.[FechaDeContratacion],E.PathImage ,E.Email,E.EnviarNotificaciones,E.Activo FROM [Catalogos].[Empleados] E WHERE (@Nombre IS NULL OR @Nombre = '' OR E.Nombre = @Nombre) END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here
SELECT Id,Familia FROM Catalogos.Familias WHERE (@Familia IS NULL OR @Familia = '' OR Familia LIKE '%'+ @Familia +'%') AND Activo = 1 END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here
SELECT E.Email FROM [Catalogos].[Empleados] E WHERE E.Activo = 1 AND E.EnviarNotificaciones = 1 END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here
SELECT Id,[Marca] FROM [Catalogos].[Marcas] WHERE (@Marcas IS NULL OR @Marcas = '' OR [Marca] LIKE '%'+ @Marcas +'%') AND Activo = 1 END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here
SELECT Id,Modelo FROM Catalogos.Modelos WHERE (@Modelo IS NULL OR @Modelo = '' OR Modelo LIKE '%'+ @Modelo +'%') AND Activo = 1 END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here
SELECT Id,[Moneda] FROM [Catalogos].[Monedas] WHERE (@Monedas IS NULL OR @Monedas = '' OR [Moneda] LIKE '%'+ @Monedas +'%') AND Activo = 1 END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here
SELECT po.Id AS IdProductoPrecioOferta ,P.[Id] AS IdProducto ,P.[Nombre] AS Producto ,P.[Description] AS DescripcionProducto ,TP.Id AS IdTipoOferta ,TP.Tipo_Oferta ,PO.[fecha_Inicio] ,PO.[Fecha_Final] ,PO.[Precio] AS PrecioOferta FROM [Productos].[Precios_Oferta] PO INNER JOIN [Catalogos].[Productos] P ON PO.Id_Producto = P.Id INNER JOIN [Productos].[Tipo_Oferta] TP ON PO.Id_Producto_Tipo_Oferta = TP.Id WHERE (@Producto IS NULL OR @Producto = '' OR P.Nombre = @Producto) END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here SELECT P.[Id] ,P.[Nombre] ,P.[RFC] ,P.[Direccion]
FROM [Catalogos].[Proveedores] P WHERE (@Nombre IS NULL OR @Nombre = '' OR P.Nombre = @Nombre) AND Activo = 1 END

